function escondeAbas(){var e=document.querySelectorAll("[data-tab-id]");for(let t=0;t<e.length;t++)e[t].classList.remove("personagens__grid__main__list__item--is-active")}function removeBotaoAtivo(){var e=document.querySelectorAll("[data-tab-button]");for(let t=0;t<e.length;t++)e[t].classList.remove("personagens__grid__nav__button--is-active")}document.addEventListener("DOMContentLoaded",function(){var e=document.querySelectorAll("[data-tab-button]");for(let t=0;t<e.length;t++)e[t].addEventListener("click",function(t){var e=t.target.dataset.tabButton,e=document.querySelector(`[data-tab-id="${e}"]`);escondeAbas(),e.classList.add("personagens__grid__main__list__item--is-active"),removeBotaoAtivo(),t.target.classList.add("personagens__grid__nav__button--is-active")})});